import "./App.css";
import { useEffect, useState } from "react";

function App() {
  const [data, setData] = useState(0);
  const [albums, setAlbums] = useState(undefined);
  const [artists, setArtists] = useState(undefined);
  const [tracks, setTracks] = useState(undefined);
  const [search, setSearch] = useState("");
  const value = "";
  const token =
    "BQAUP3bcvioOuMWOptl_hir6JSxX74ODL_1g_V2ZtviTI2nJ3szuPyuzmokSb9Nfhsqz_F3ZdGmmX9BevqOwVyFNKVhilojNJK6EOVQNl3OxEOH08LrR0DBn_DbuKLA5_QFXdoTPoO4FEsrU5y0wMiYO5burevymYf0";
  // const search = "ed sheeran";
  async function fetchData() {
    const response = await fetch(
      `https://api.spotify.com/v1/search?q=${search}&type=album,track,artist`,
      {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      }
    );
    const info = await response.json();
    return await info;
  }
  useEffect(() => {
    fetchData().then((info) => {
      if (
        info.albums !== undefined &&
        info.artists !== undefined &&
        info.tracks !== undefined
      ) {
        setData(info);
        setAlbums(info.albums.items.slice(0, 5));
        setArtists(info.artists.items.slice(0, 5));
        setTracks(info.tracks.items.slice(0, 5));
      }
    });
  }, [search]);
  let albumDisplay = undefined;
  let artistDisplay = undefined;
  let trackDisplay = undefined;

  if (albums !== undefined && artists !== undefined && tracks !== undefined) {
    // display albums
    albumDisplay = albums.map((album, index) => (
      <div>
        <a href={album.external_urls.spotify}>{album.name}</a>
      </div>
    ));
    // display artists
    artistDisplay = artists.map((artist, index) => (
      <div>
        <a href={artist.external_urls.spotify}>{artist.name}</a>
      </div>
    ));
    // display tracks
    trackDisplay = tracks.map((track, index) => (
      <div>
        <a href={track.external_urls.spotify}>{track.name}</a>
      </div>
    ));
  }

  const HandleResponse = (e) => {
    e.preventDefault();
  };
  return (
    <div className="App">
      <div>
        <h1>Search</h1>
        <form onSubmit={(e) => HandleResponse(e)}>
          <input
            type="text"
            value={data}
            onChange={(e) => {
              setSearch(e.target.value);
            }}
          ></input>
          <br />
          <button>Submit</button>
        </form>
      </div>
      <div>
        <h1>Albums</h1>
        <div>{albumDisplay}</div>
        <h1>Artists</h1>
        <div>{artistDisplay}</div>
        <h1>Tracks</h1>
        <div>{trackDisplay}</div>
      </div>
    </div>
  );
}

export default App;
