import "./App.css";
import { useEffect, useState } from "react";

function App() {
  const [data, setData] = useState(0);
  const [albums, setAlbums] = useState(undefined);
  const [artists, setArtists] = useState(undefined);
  const [tracks, setTracks] = useState(undefined);
  const [search, setSearch] = useState("");
  const [formVal, setformVal] = useState("");
  const [userList, setUserList] = useState([]);
  const token =
    "BQDC0twaomaB7FbZwxAfRKb7vkECmFr6hK4neLrh6TwzfzqTTK_11Xlw7bG7v5OPjcOALOeAvtvYyfm2YkHZiY2pHo97arK1LJjlQAtFnRXNT3todhPEnq71QYmbxKr2VFwQYUuBeiQ9B4IGo1Qic63VtglGmzlDEMQ";
  const HandleResponse = (e) => {
    e.preventDefault();
    setSearch(formVal);
    setformVal("");
    if (albums !== undefined) {
      for (let i = 0; i < albums.length; i++) {
        if (document.getElementById(albums[i].name) !== null) {
          console.log(albums[i].name);

          document.getElementById(albums[i].name).style.display = "block";
        }
      }
      for (let i = 0; i < userList.length; i++) {
        console.log(userList[i].name);
        if (document.getElementById(userList[i].name) !== null) {
          document.getElementById(userList[i].name).style.display = "none";
        }
      }
    }
  };
  async function fetchData() {
    try {
      const response = await fetch(
        `https://api.spotify.com/v1/search?q=${search}&type=album,track,artist`,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      let info = await response.json();
      return await info;
    } catch (e) {
      console.log("error");
    }
  }

  useEffect(() => {
    fetchData()
      .then((info) => {
        if (
          info.albums !== undefined &&
          info.artists !== undefined &&
          info.tracks !== undefined
        ) {
          setData(info);
          setAlbums(info.albums.items.slice(0, 5));
          setArtists(info.artists.items.slice(0, 5));
          setTracks(info.tracks.items.slice(0, 5));
        }
      })
      .catch((err) => {
        console.log(err);
      });
  }, [search]);
  let albumDisplay = undefined;

  if (albums !== undefined && artists !== undefined && tracks !== undefined) {
    // display albums
    albumDisplay = albums.map((album, index) =>
      album.album_type == "album" ? (
        <div class="album" key={index}>
          <div class="album-content" id="cover">
            <div class="image">
              <img src={album.images[0].url}></img>
            </div>
          </div>
          <div class="album-content" id="text">
            <div class="text">
              <a href={album.external_urls.spotify}>{album.name}</a>
            </div>
          </div>
          <div class="album-content" id="button">
            <div class="button">
              <button
                class="btn-add"
                style={{ display: "block" }}
                id={album.name}
                onClick={() => AddtoList(album)}
              >
                +
              </button>
            </div>
          </div>
        </div>
      ) : null
    );
  }

  const AddtoList = (value) => {
    setUserList((userList) => [...userList, value]);
    document.getElementById(value.name).style.display = "none";
  };

  useEffect(() => {
    console.log(userList);
  }, [userList]);

  return (
    <div className="App">
      <div>
        <h1>Search</h1>
        <form onSubmit={(e) => HandleResponse(e)}>
          <input
            type="text"
            value={formVal}
            onChange={(e) => {
              setformVal(e.target.value);
            }}
          ></input>
          <br />
          <button>Submit</button>
        </form>
      </div>
      <div>
        <div class="results">
          <div class="result">
            <h1>Albums</h1>
            {albumDisplay}
          </div>
        </div>
      </div>
      <h1>My List</h1>
      <div class="results">
        <div class="result">
          {userList.map((album, index) => (
            <div class="album" key={index}>
              <div class="album-content" id="cover">
                <div class="image">
                  <img src={album.images[0].url}></img>
                </div>
              </div>
              <div class="album-content" id="text">
                <div class="text">
                  <a href={album.external_urls.spotify}>{album.name}</a>
                </div>
              </div>
              <div class="album-content" id="button"></div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
