import "./App.css";
import { useEffect, useState } from "react";

function App() {
  const [data, setData] = useState(0);
  const [albums, setAlbums] = useState(undefined);
  const [artists, setArtists] = useState(undefined);
  const [tracks, setTracks] = useState(undefined);
  const [search, setSearch] = useState("");
  const [formVal, setformVal] = useState("");
  const [userList, setUserList] = useState([]);
  const token =
    "BQA5xcrOY2j885VARqFrg6T_huKS48vUkx-lYsazLGEqCIDizgn8mKWgWZzQcFx86cDvedK-pdGlfVoEGFetpGxNyE_RH7vJQrubZZRpFnESdJDLfM_1SBlizPeOdSjjLFZq0nhZchusyAIwFEWOMjNn3tp9MI-X-X0";
  const HandleResponse = (e) => {
    e.preventDefault();
    setSearch(formVal);
    setformVal("");
  };
  async function fetchData() {
    try {
      const response = await fetch(
        `https://api.spotify.com/v1/search?q=${search}&type=album,track,artist`,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      let info = await response.json();
      return await info;
    } catch (e) {
      console.log("error");
    }
  }

  useEffect(() => {
    fetchData()
      .then((info) => {
        if (
          info.albums !== undefined &&
          info.artists !== undefined &&
          info.tracks !== undefined
        ) {
          setData(info);
          setAlbums(info.albums.items.slice(0, 5));
          setArtists(info.artists.items.slice(0, 5));
          setTracks(info.tracks.items.slice(0, 5));
        }
      })
      .catch((err) => {
        console.log(err);
      });
  }, [search]);
  console.log(albums);
  let albumDisplay = undefined;
  let artistDisplay = undefined;
  let trackDisplay = undefined;

  if (albums !== undefined && artists !== undefined && tracks !== undefined) {
    // display albums
    albumDisplay = albums.map((album, index) => (
      // if album.album_type == "album" {
      <div key={index}>
        <a href={album.external_urls.spotify}>{album.name}</a>
        <button id={index} onClick={() => AddtoList(album, index)}>
          +
        </button>
      </div>
    ));
    // display artists
    // artistDisplay = artists.map((artist, index) => (
    //   <div key={index}>
    //     <a href={artist.external_urls.spotify}>{artist.name}</a>
    //     <button onClick={() => AddtoList(artist)}>+</button>
    //   </div>
    // ));
    // display tracks
    // trackDisplay = tracks.map((track, index) => (
    //   <div key={index}>
    //     <a href={track.external_urls.spotify}>{track.name}</a>
    //     <button onClick={() => AddtoList(track)}>+</button>
    //   </div>
    // ));
  }

  const AddtoList = (value, index) => {
    setUserList((userList) => [...userList, value]);
    document.getElementById(index).style.display = "none";
  };

  useEffect(() => {
    console.log(userList);
  }, [userList]);

  return (
    <div className="App">
      <div>
        <h1>Search</h1>
        <form onSubmit={(e) => HandleResponse(e)}>
          <input
            type="text"
            value={formVal}
            onChange={(e) => {
              setformVal(e.target.value);
            }}
          ></input>
          <br />
          <button>Submit</button>
        </form>
      </div>
      <div>
        <div class="results">
          <div class="result">
            <h1>Albums</h1>
            {albumDisplay}
          </div>
          {/* <div class="result">
            <h1>Artists</h1>
            {artistDisplay}
          </div>
          <div class="result">
            <h1>Tracks</h1>
            {trackDisplay}
          </div> */}
        </div>
      </div>
      <h1>My List</h1>
      <div>
        {userList.map((item, index) => (
          <div>
            <a key={index} href={item.external_urls.spotify}>
              {item.name}
            </a>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
